import React, { useState } from 'react'

const Search = () => {
    const [text, setText] = useState('')
    const onChange = (q) => {
        setText(q)
        getQuery(q)
    }

    return (
        <section className="search">
            <form>
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search characters"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    autofocus />
            </form>
        </section>
    )
}

export default Search
